// src/nextjs.ts
module.exports = function withCloud(nextConfig = {}) {
  return Object.assign({}, nextConfig, {
    distDir: process.env.NODE_ENV === "production" ? ".next" : ".next-dev",
    experimental: {
      outputStandalone: true
    },
    webpack(config) {
      config.externals.push("@serverless/cloud");
      return config;
    }
  });
};
